// Tool implementation
async function getExampleRepositoryStructure({
  handle,
}: InferSchema<typeof schema>) {
  try {
    const repo = templateRepos.find((t) => t.handle === handle);

    if (!repo) {
      return {
        isError: true,
        content: [
          {
            type: "text",
            text: `Repository with handle '${handle}' not found. Available handles: ${templateRepos
              .map((t) => t.handle)
              .join(", ")}`,
          },
        ],
      };
    }

    return {
      content: [
        {
          type: "text",
          text: repo.structure,
        },
      ],
    };
  } catch (error) {
    return {
      isError: true,
      content: [
        {
          type: "text",
          text: `Error: ${
            error instanceof Error ? error.message : String(error)
          }`,
        },
      ],
    };
  }
}

// Export the wrapped function with logging
export default withLogging("get_example_content_structure", getExampleRepositoryStructure);
